import PageOptions from "../config/PageOptions.vue";
import Vue from "vue";
import axios from "axios";
import { ClientTable } from "vue-tables-2";
import { all } from "q";
import { setTimeout } from "timers";

Vue.use(ClientTable);

var rawData = [];

export default {
  components: {
    ClientTable
  },
  data() {
    return {
      tableData: [],
      columns: [
        "Index",
        "Insurance ID",
        "Username",
        "Product ID",
        "Project ID",
        "Price Total",
        "Date",
        "Status",
        "Remark"
      ],
      options: {
        pagination: { chunk: 5 },
        sortIcon: {
          is: "fa-sort",
          base: "fas",
          up: "fa-sort-up",
          down: "fa-sort-down"
        }
      }
    };
  },
  created() {
    PageOptions.pageWithTopMenu = true;
    PageOptions.pageWithoutSidebar = true;

    this.updateData();
    this.retryData();
  },
  beforeRouteLeave(to, from, next) {
    PageOptions.pageWithTopMenu = false;
    PageOptions.pageWithoutSidebar = false;
    next();
  },
  methods: {
    updateData() {
      rawData = [];
      axios
        .get("/check_all_insurance/")
        .then(res => {
          if (res.data != null) {
            var response = JSON.parse(JSON.stringify(res.data));
            for (var i = 0; i < response.length; i++) {
              rawData[rawData.length] = {
                "Insurance ID": response[i].id,
                Username: response[i].username,
                "Product ID": response[i].product_id,
                "Project ID": response[i].project_id,
                "Price Total": response[i].amount_of_money,
                Date: response[i].date,
                Status: response[i].status,
                Remark: response[i].remark
              };
            }
          }
          if (rawData != null) {
            this.tableData = rawData.map((item, index) => {
              item["Index"] = index;
              return item;
            });
          }
        })
        .catch(err => console.log(err));
    },
    show(group, type = "") {
      const text = "New insurance order awarting for process!";
      this.$notify({
        group,
        text,
        type,
        data: {
          randomNumber: Math.random()
        }
      });
    },
    checkRemark(row) {
      console.log(row);
      alert(`Editing row id: ${row.Remark}`);
    },
    retryData() {
      var timer;
      axios
        .get("/check_all_insurance/")
        .then(res => {
          if (res.data != null) {
            var response = JSON.parse(JSON.stringify(res.data));
            this.show("bottom-right", "success");
            if (response.length != rawData.length) {
              this.show("bottom-right", "success");
            }
            timer = setInterval(() => {
              clearInterval(timer);
              this.retryData();
            }, 10000);
          } else {
            console.log("Periodlically update failed.");
            return;
          }
        })
        .catch(err => console.log(err));
    }
  }
};
